import React, { useState } from "react";
import Card from "../UI/Card";
import classes from "./AddNewMeal.module.css";

const AddNewMeal = (props) => {
  const [meal, setMeal] = useState({
    id: Date.now,
    name: "",
    description: "",
    price: 0,
  });

  const [isShow,setIsShow] = useState(false);

  const nameChangeHandler = (event) => {
    const dummy = { ...meal, name: event.target.value };

    setMeal(dummy);
  };
  const descChangeHandler = (event) => {
    const dummy = { ...meal, description: event.target.value };

    setMeal(dummy);
  };
  const priceChangeHandler = (event) => {
    const dummy = { ...meal, price: +event.target.value };

    setMeal(dummy);
  };
  const formSubmitHandler = (event) => {
    event.preventDefault();
    props.uploadData({ ...meal, id: Date.now() });
  };
  let classn;
  const btnClick = () =>{
    setIsShow(!isShow);
    if(isShow){
      classn = classes.show;
    }
    else {
      classn = classes.hide;
    }
  }
  return (
    <React.Fragment>
      <button onClick={btnClick}>toggle</button>
      <section className={`${classn}`}>
        <Card>
          <form onSubmit={formSubmitHandler}>
            <input
              onChange={nameChangeHandler}
              placeholder="name"
              type="text"
            />
            <input
              onChange={descChangeHandler}
              placeholder="description"
              type="text"
            />
            <input
              onChange={priceChangeHandler}
              placeholder="price"
              type="number"
            />
            <button type="submit"> Add new Item</button>
          </form>
        </Card>
      </section>
    </React.Fragment>
  );
};

export default AddNewMeal;
